sp_widget.client_script:
api.controller=function($rootScope, $scope) {
	/* widget controller */
	var c = this;

	c.selectedNoteIndex = null;

	c.selectItem = function(idx){
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
		c.selectedNoteIndex = idx;
	}

	$rootScope.$on('updateNote', function(event, noteTitle, noteBody){
		if(c.selectedNoteIndex !== null){
			c.data.notes[c.selectedNoteIndex].title = noteTitle;
			c.data.notes[c.selectedNoteIndex].note = noteBody;
		}
	})


};

sp_widget.script:
(function() {

	data.notes = [];
	var grNotes = new GlideRecord('x_snc_createnotes_note');
	grNotes.addQuery('user', gs.getUserID());
	grNotes.orderByDesc('sys_created_on');
	grNotes.query();

	while(grNotes.next()){
		var noteObj = {};

		$sp.getRecordDisplayValues(noteObj, grNotes, 'number,title,sys_id');

		noteObj.note = grNotes.getValue('note').slice(0, 20);
		data.notes.push(noteObj);
	}


})();
